// import logo from './logo.svg';
import React from 'react';
import './Quotes.scss';
class Quotes extends React.Component{
constructor(props){
  super(props)
  this.state={
    randomNo:0,
    quotesArry:[],
  }
  this.callNewQoute=this.callNewQoute.bind(this);
}
componentDidMount(){
  fetch("https://type.fit/api/quotes")
  .then(resp=>resp.json ())
  .then(result =>{
    this.setState({quotesArry:result})
  },(error)=>console.log(error)
  );
}
callNewQoute=()=>{
  this.setState({
    randomNo:Math.floor(Math.random()*this.state.quotesArry.length)
  })
}
render(){
const {quotesArry,randomNo}=this.state;
return (
  <div className="wrapper">
    {quotesArry[randomNo] && (
      <div id="quote-box" className="quotebox">
        <p id="text" className="text"> <i className="fa fa-quote-left">  {quotesArry[randomNo].text} </i></p>
        { quotesArry[randomNo].author? <p id="author" className="author">  - {quotesArry[randomNo].author}</p>: <p  className="author"></p>}
        <a target="_top" href={`https://twitter.com/intent/tweet?hashtags=quotes&text=${quotesArry[randomNo].text}`} > <i className="fa fa-twitter"> </i></a>
        <button onClick={this.callNewQoute} id="new-quote" className="newQuote"> New Qoute</button>
    </div>)
    }
  </div>)
  }
}

export default Quotes;
