// import logo from './logo.svg';
import React from 'react';
import './Quotes.css';
class Quotes extends React.Component{
constructor(props){
  super(props)
  this.state={
    quotesArry:[],
  }
}
componentDidMount(){
  fetch("https://type.fit/api/quotes")
  .then(resp=>resp.json ())
  .then(result =>{
    this.setState({quotesArry:result})
  },(error)=>console.log(error)
  );
}
render(){
const randomIndex =Math.floor(Math.random()*this.state.quotesArry.length);
const {quotesArry}=this.state;
return (
   quotesArry[randomIndex] &&
    <div>
   <p> {quotesArry[randomIndex].text}</p>
    <span> {quotesArry[randomIndex].author}</span>
    </div>



  )}

}

export default Quotes;
