import React from 'react';
import './Quotes.css';
class Quotes extends React.Component{
constructor(props){
  super(props)
  this.state={
    quotes:[],
  }
}
componentDidMount(){
  let qoutes=[]
  fetch("https://type.fit/api/quotes")
  .then(function(response) {
    return response.json();
  })
  .then(function(data) {
    qoutes=data
  });
  this.setState({quotes:qoutes});
}


render(){return (
  <section>
      {this.state.quotes.map((item,key)=>{
         if( key<=10){
          return <b key={key}>{item}</b>
         }
      })}
  </section>
);}


}

export default Quotes;
